mapper.select:
select b.*, rownum rnum5 from (select a.*,rownum rnum from (SELECT cus.*,COALESCE(c,0) as myCount from customizing cus left join (select custom_num,count(*)c from u_like group by custom_num) e on cus.custom_num = e.custom_num)a)b
WHERE rnum >= #{start} AND rnum <= #{end}
order by MYCOUNT desc
